package aula1;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 1;
		
		switch(num) {
			case 1:
				System.out.println("um");
				break;
			case 2:
				System.out.println("Dois");
				break;
				default:
					System.out.println("Numero grande ou inexistente");
		}
	}

}
