public class numeroMultiplica {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//imprimindo somente os numeros pares de 
		int multiplicador = 3;
		
		for(int i = 0; i <=10; i++) {
			
			if(i % 2 ==0) {
				System.out.println(i * multiplicador);
			}
			
			
		}
	}

}
